<template>	<div class="header" v-show="headerShow">		<div class="left-back" @click="backChang" v-show="backShow"><i></i></div>		<h1 v-text="title"></h1>		<div v-show="historyShow" class="right-colmun righttxt" @click="historyHandle">历史</div>		<div class="right-colmun right-icon" v-show="messageShow" @click="message">			<i class="icomessage"></i>			<span></span>		</div>	</div></template><script>	export default {		name: "ComHeader",		props:{			title:{				type:String,				default:''			}		},		data(){			return{				historyShow:false,				headerShow:true,				messageShow:false,				backShow:true,			}		},		watch:{			'$route'(){				this.historyShow=this.$route.name==='game' || this.$route.name==='read';				this.headerShow=!(this.$route.name==='task' || this.$route.name==='goldcoin' || this.$route.name==='balance' || this.$route.name==='foxgrass'|| this.$route.name==='shareindex'|| this.$route.name==='sharemoney'|| this.$route.name==='sharecoin');				this.messageShow=this.$route.name==='user';				switch(this.$route.name){					case 'game':						this.backShow=false;						break;					case 'read':						this.backShow=false;						break;					case 'task':						this.backShow=false;						break;					case 'user':						this.backShow=false;						break;					default:						this.backShow=true;				}			}		},		methods:{			historyHandle(){				this.$emit('history-chang');			},			message(){				this.$router.push({name:'message'})			},			backChang(){				this.$router.go(-1);			}		},		computed:{		},		mounted(){		}	}</script>