<template>	<div class="foxball">		<div class="foxball-main">			<div v-show="!textShow">				<div class="photo">					<img src="../assets/icon-active-foxball.png" alt="" />				</div>				<div class="photo active">					<img src="../assets/icon-normal-foxball.png" alt="" :style="setInitNum" />				</div>			</div>			<div class="text" v-show="textShow">				金币<br />+40			</div>		</div>	</div></template><script>	import { MessageBox } from 'mint-ui';	export default {		name: "FoxBall",		data(){			return{				textShow:false,				initNum:1,				time:null,			}		},		computed:{			setInitNum(){				return `-webkit-clip-path:inset(0 0 ${this.initNum * 10}% 0)`			}		},		methods:{			initBall(){				this.time=setInterval(()=>{					this.initNum+=1;					if(this.initNum>=10){						clearInterval(this.time);						this.textShow=true;					}				},1000)			},			ball(){				if(!this.textShow) return;				MessageBox.alert('你已领取40金币').then(action=>{					if(action){						this.initNum=1;						this.textShow=false;						this.initBall();					}				});			}		},		mounted(){			this.initBall();		}	}</script><style scoped></style>