<template>	<button class="phonecode" type="button" :disabled="btndisabled">{{second?second:btntext}}</button></template><script>	export default {		name: "PhonecodeBtn",		props:{			/*btndisabled:{				type:Boolean,				default:false			},*/			/*second:{				type:Number,				default:10			},			btntext:{				type:String,				default:'获取验证码'			}*/		},		data(){			return{				btndisabled:false,				second:0,				btntext:'获取验证码'			}		},		methods:{			run(){				this.second=10;				this.btndisabled=true;				this.timer();			},			timer(){				if(this.second>0){					this.second--;					setTimeout(this.timer,1000);				}else{					this.btndisabled=false;				}			}		}	}</script><style scoped></style>