<template>	<div>		<div class="common-bg" v-show="bgType">			<div class="red-lopes" v-show="lopesType">				<div class="red-lopes-btn" @click="handleOpen"></div>			</div>			<transition name="fade">				<red-lopes-open :lopesShow="lopesShow" @chang="handleExit"></red-lopes-open>			</transition>		</div>	</div></template><script>	import RedLopesOpen from './RedLopesOpen'	export default {		name: "RedLopes",		props:{			bgType:{				type:Boolean,				default:false			}		},		data(){			return{				lopesType:true,				lopesShow:false			}		},		methods:{			handleOpen(){				this.lopesType=false;				this.lopesShow=true			},			handleExit(){				this.$emit('lopes-show');				this.lopesShow=false			}		},		components:{			RedLopesOpen		}	}</script><style scoped rel="stylesheet" lang="scss">	.fade-enter,.fade-leave-to{		transition:all 1s ease;		}	.fade-enter-to,.fade-leave{		transform:translateX(100px);		opacity:0;		}</style>