<template>	<div class="tabbar-item" :class="[iconShow?'active':'']" @click="handleChang">			<span v-show="!iconShow">				<slot name="normalImg"></slot>			</span>		<span v-show="iconShow">				<slot name="activeImg"></slot>			</span>		<strong v-text="text"></strong>	</div></template><script>	export default {		name: "NavItem",		props:{			text:{				type:String,				default:''			},			page:{				type:String,				default:'game'			},			curren:{				type:String,				default:'game'			}		},		methods:{			handleChang(){				this.$router.push(`/${this.page}`);				this.$emit('chang',this.page);			}		},		computed:{			iconShow(){				return (this.curren === this.page)?1:0;			}		}	}</script><style rel="stylesheet" lang="scss">	@import "./scss/tabbar";</style>