<template>	<div class="tabbar" v-show="tabberShow">		<nav-item v-for="(item,index) in tabBar" :text="item.text" :page="item.page" :curren="curren" @chang="getPage" :key="item.page">			<img :src="item.normalImg" slot="normalImg" />			<img :src="item.activeImg" slot="activeImg" />		</nav-item>	</div></template><script>	import NavItem from './NavItem'	export default {		name: "TabBar",		data(){			return{				curren:'game',				tabBar:[					{text:'游戏',page:'game',normalImg:require('./img/ico-game-normal.png'),activeImg:require('./img/ico-game-active.png')},					{text:'阅读',page:'read',normalImg:require('./img/ico-read-normal.png'),activeImg:require('./img/ico-read-active.png')},					{text:'任务',page:'task',normalImg:require('./img/ico-task-normal.png'),activeImg:require('./img/ico-task-active.png')},					{text:'我的',page:'user',normalImg:require('./img/ico-user-normal.png'),activeImg:require('./img/ico-user-active.png')}				]			}		},		watch:{			'$route'(){				this.curren=this.$route.name;			}		},		computed:{			tabberShow(){				return this.$route.path === '/game' || this.$route.path === '/read' || this.$route.path === '/task' || this.$route.path === '/user'			}		},		methods:{			getPage(page){				this.curren=page;			}		},		components:{			NavItem		},		mounted(){		}	}</script><style rel="stylesheet" lang="scss">	@import "./scss/tabbar";</style>