<template>	<section>		<div class="login">			<div class="login-list">				<ul>					<li>						<div class="text">账号</div>						<div class="inputcolumn">							<mt-field  placeholder="请输入用户名" :attr="{maxlength:30}" v-model="params.phone" ></mt-field>						</div>					</li>					<li>						<div class="text">登录密码</div>						<div class="inputcolumn">							<mt-field type="password"  placeholder="输入密码" :attr="{maxlength:20}" v-model="params.pwd"></mt-field>						</div>					</li>				</ul>			</div>			<div class="login-agreen">				<mt-checklist v-model="agreenValue" :options="agreenOption"></mt-checklist>			</div>			<div class="common-btn">				<button type="button" @click="handleSubmit">登录</button>			</div>			<div class="login-text">				<div class="text01"><router-link to="">忘记密码？</router-link></div>				<div class="text02"><router-link to="">注册</router-link></div>			</div>		</div>	</section></template><script>	import { Field,Checklist,Toast } from 'mint-ui';	export default {		name: "Login",		data(){			return{				agreenValue:['值A'],				agreenOption:[					{						label: '同意超级狐狸注册协议',						value: '值A'					},				],				params:{					phone:'',					pwd:''				}			}		},		methods:{			handleSubmit(){				if(this.params.phone===''){					Toast('用户名不能为空');					return false				}else if (this.params.pwd===''){					Toast('密码不能为空');					return false				}else{					this.$http('http://www.weirdfoxes.cn:99/ComService.svc/Login',{data:this.params}).then(res=>{						console.log('登录======>',res);					})				}			}		}	}</script><style rel="stylesheet" lang="scss">	@import "./scss/login";</style>