<template>	<section class="comindex">		<div class="banner">			<mt-swipe :auto="4000">				<mt-swipe-item v-for="swipItem in bannerModels">					<img :src="swipItem.img" alt="" />				</mt-swipe-item>			</mt-swipe>		</div>		<div class="read">			<div class="common-title">				<h2>					<strong>新书推荐</strong>					<span v-show="newRead.menuModel"><router-link :to="{name:'readList'}">查看更多</router-link></span>				</h2>			</div>			<div class="read-list">				<ul>					<li v-for="newItem in newRead.content">						<router-link to="">							<div class="photo">								<img :src="newItem.img" alt="" />							</div>							<h3 v-text="newItem.name"></h3>							<p v-text="newItem.author"></p>						</router-link>					</li>				</ul>			</div>		</div>		<div class="read">			<div class="common-title">				<h2>					<strong>书频精选</strong>					<span v-show="fineRead.menuModel"><router-link :to="{name:'readList'}">查看更多</router-link></span>				</h2>			</div>			<div class="read-list">				<ul>					<li v-for="fineItem in fineRead.content">						<router-link to="">							<div class="photo">								<img :src="fineItem.img" alt="" />							</div>							<h3 v-text="fineItem.name"></h3>							<p v-text="fineItem.author"></p>						</router-link>					</li>				</ul>			</div>		</div>		<div class="footer-ad">			<div class="photo">				<mt-swipe :auto="6000" :show-indicators="false">					<mt-swipe-item v-for="footerswip in footerAd">						<img :src="footerswip.img" alt="" />					</mt-swipe-item>				</mt-swipe>			</div>		</div>		<tab-bar></tab-bar>	</section></template><script>	import TabBar from '@/components/tabbar/TabBar'	import { Swipe, SwipeItem,Indicator,Toast } from 'mint-ui';	export default {		name: "Read",		data(){			return{				bannerModels:[],    //banner图				newRead:{},  //新书推荐				fineRead:{},    //书频精选				footerAd:[]			}		},		methods:{			getData(){				Indicator.open({					text: '加载中...',					spinnerType: 'fading-circle'				});				this.$http('https://47867514.ngrok.io/novel/wall').then(res=>{					Indicator.close();					let data=res.data;					let columnModels=data.columnModels;					this.bannerModels=data.bannerModels;					this.footerAd=data.footer;					columnModels.map((item,index)=>{						if(item.name==='新书推荐'){							this.newRead=item						}						if(item.name==='书频精选'){							this.fineRead=item;						}					});					console.log('新书推荐====>',this.newRead)					console.log('书频精选====>',this.fineRead)					console.log('res',res)				}).catch(err=>{					Indicator.close();					Toast(err.msg);				})			}		},		components:{			TabBar		},		mounted(){			this.getData();		}	}</script><style rel="stylesheet" lang="scss">	@import "./scss/read";</style>