<template>	<section>		<div class="header">			<div class="left-back" @click="backGo"><i></i></div>			<div class="header-menu">				<ul>					<li v-for="item in textList" v-text="item.txt" :class="[item.page===current?'active':'']" @click="handleChang(item.page)"></li>				</ul>			</div>		</div>		<router-view />	</section></template><script>	export default {		name: "Money",		data(){			return{				current:this.$route.name || 'goldcoin',				textList:[					{txt:'金币',page:'goldcoin'},					{txt:'零钱',page:'balance'},					{txt:'狐狸草',page:'foxgrass'}				]			}		},		methods:{			backGo(){				this.$router.push('/user');			},			handleChang(path){				this.current=path;				this.$router.push(`/user/money/${path}`);			}		},		mounted(){		}	}</script><style rel="stylesheet" lang="scss"></style>