<template>	<section class="share">		<div class="header">			<div class="left-back" @click="backGo"><i></i></div>			<div class="header-menu">				<ul>					<li v-for="item in textList" v-text="item.txt" :class="[item.page===current?'active':'']" @click="handleChang(item.page)"></li>				</ul>			</div>		</div>		<router-view />	</section></template><script>	export default {		name: "share",		data(){			return{				current:this.$route.name || 'shareindex',				textList:[					{txt:'邀请',page:'shareindex'},					{txt:'现金奖励',page:'sharemoney'},					{txt:'金币奖励',page:'sharecoin'}				]			}		},		methods:{			backGo(){				this.$router.push('/user');			},			handleChang(path){				this.current=path;				this.$router.push(`/user/share/${path}`);			}		}	}</script><style rel="stylesheet" lang="scss">	@import "./scss/share";</style>